import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Epi } from 'src/app/models/epi.model';

@Component({
  selector: 'app-epi-cadastrar',
  templateUrl: './epi-cadastrar.component.html',
  styleUrls: ['./epi-cadastrar.component.css']
})
export class EpiCadastrarComponent {
  constructor(private client: HttpClient) {}
  epi: Epi = {
    descricao: "",
    c_A: "",
    quantidade: null,
    epiID: "",
  };

  onSubmit(): void {
    this.client.post<Epi>("https://localhost:7144/EpiList/EPI/cadastrar", this.epi).subscribe({
      next: (data) => {
        console.log(data);
      },
      error: (erro) => {
        console.log(erro);
      },
    });
  }
}
<h1>Cadastro de Epi</h1>
<form (ngsubmit)="submitForm()">
    <div>
      <label for="epiID">ID do EPI:</label>
      <input type="text" id="epiID" name="epiID" [(ngModel)]="epi.epiID" required>
    </div>

    <div>
      <label for="descricao">Descrição:</label>
      <input type="text" id="descricao" name="descricao" [(ngModel)]="epi.descricao" required>
    </div>

    <div>
      <label for="c_a">Código CA:</label>
      <input type="text" id="c_a" name="c_a" [(ngModel)]="epi.c_a" required>
    </div>

    <div>
      <label for="quantidade">Quantidade:</label>
      <input type="number" id="quantidade" name="quantidade" [(ngModel)]="epi.quantidade" required>
    </div>

    <button type="submit">Salvar</button>
  </form>
