import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Epi } from 'src/app/models/epi.model';
import { Usuario } from 'src/app/models/usuario.model';

@Component({
  selector: 'app-usuario-atualizar',
  templateUrl: './usuario-atualizar.component.html',
  styleUrls: ['./usuario-atualizar.component.css']
})
export class UsuarioAtualizarComponent {
  usuarioId!: number;
  episUsuario: Epi[] = []
  usuarioForm: FormGroup; // Formulário reativo
  episDisponiveis: Epi[] = [];
  episDoUsuario: Epi[] = [];
  epiSelecionado: Epi | null = null;
  constructor(private client: HttpClient,private router: Router, private route: ActivatedRoute, private fb: FormBuilder) 
  {
    this.usuarioForm = this.fb.group({
      nome: [''],
      email: [''],
      telefone: [''],
      cpf: [''],
      senha: [''],
      cargo: [''],
      equipeID: [0]
    });
  }
  ngOnInit(): void{
    this.usuarioId = this.route.snapshot.params['id'];
    this.client.get<Usuario>("https://localhost:7144/EpiList/Usuario/"+this.usuarioId)
    .subscribe({
      next: (usu) => {
        console.table(usu);
        if (usu.epis) {
          this.episDoUsuario = usu.epis.map((epiItem) => ({
            epiID: epiItem.epiId, // ou epiItem.epiId se for uma string
            descricao: epiItem.descricao,
            // Preencha as outras propriedades com valores padrão ou deixe-as em branco
            c_A: '', // Exemplo: Preencha com um valor padrão se necessário
            quantidade: 0, // Exemplo: Preencha com um valor padrão se necessário
            // Outras propriedades do tipo Epi, se houver
          }));
        }
        this.usuarioForm.patchValue({
          nome: usu.nome,
          email: usu.email,
          telefone: usu.telefone,
          cpf: usu.cpf,
          senha: usu.senha,
          cargo: usu.cargo,
          equipeID: [usu.equipe]
        });
      }, 
      error: (erro) => {
        console.log(erro);
      }
    })
        // Busque a lista de EPIS disponíveis
        this.client.get<Epi[]>("https://localhost:7144/EpiList/EPI/listar").subscribe((epis: Epi[]) => {
          //this.episDisponiveis = epis;
          this.episDisponiveis = epis.filter(epi => (epi.quantidade ?? 0) > 1);
          // Inicialize a variável episSelecionados para cada EPI

        });
  }

  salvarEdicao(){
    const dadosEditados = this.usuarioForm.value;
    console.log(dadosEditados);
    this.client.put<Usuario>("https://localhost:7144/EpiList/Usuario/"+this.usuarioId+"/Atualizar", dadosEditados).subscribe({
      next: (data) => {
        console.log(data);
        this.router.navigate(['pages/Usuario/listar']);
      },
      error: (erro) => {
        console.log(erro);
      },
    });
  }

  adicionarEpis() {

  }


}
<div>
<form [formGroup]="usuarioForm" (ngSubmit)="salvarEdicao()">
    <div>
      <label for="nome">Nome:</label>
      <input type="text" id="nome" formControlName="nome">
    </div>
    <div>
      <label for="email">Email:</label>
      <input type="email" id="email" formControlName="email">
    </div>
    <div>
      <label for="telefone">Telefone:</label>
      <input type="tel" id="telefone" formControlName="telefone">
    </div>
    <div>
      <label for="cpf">CPF:</label>
      <input type="text" id="cpf" formControlName="cpf">
    </div>
    <div>
      <label for="senha">Senha:</label>
      <input type="text" id="senha" formControlName="senha">
    </div>
    <div>
      <label for="cargo">Cargo:</label>
      <input type="text" id="cargo" formControlName="cargo">
    </div>
    <div>
      <label for="equipeID">Equipe ID:</label>
      <input type="number" id="equipeID" formControlName="equipeID">
    </div>
    <button type="submit">Salvar Edição</button>
  </form>
</div>
<div>
  <p>adicionar Epi</p>
  <button (click)="adicionarEpis()">Adicionar Epi</button>
</div>
