import { Component } from '@angular/core';

@Component({
  selector: 'app-usuario-deletar',
  templateUrl: './usuario-deletar.component.html',
  styleUrls: ['./usuario-deletar.component.css']
})
export class UsuarioDeletarComponent {

  deletarEpi() {
    const id = this.route.snapshot.params['id']; // Obtém o ID do EPI da rota

    this.client.delete<string>("https://localhost:7144/EpiList/EPI/Deletar/" + id)
      .subscribe({
        // Requisição com sucesso
        next: (e) => {
          console.log(e);
          // Realize qualquer outra ação após a exclusão, se necessário
          this.router.navigate(['pages/Epi/listar']);
        },
        // Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      });
  }
  cancelar(){
    this.router.navigate(['pages/Epi/listar']);
  }
}
<h2>Confirmação de Exclusão</h2>
<p>Você tem certeza de que deseja excluir este Usuario?</p>
<button (click)="deletarUsuario()">Confirmar Exclusão</button>
<button (click)="cancelar()">Cancelar</button>
