import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Unidade } from 'src/app/models/unidade.model';
import { Usuario } from 'src/app/models/usuario.model';

@Component({
  selector: 'app-unidade-cadastrar',
  templateUrl: './unidade-cadastrar.component.html',
  styleUrls: ['./unidade-cadastrar.component.css']
})
export class UnidadeCadastrarComponent {

  unidadeNome?: string;
  gestorSelecionado?: number;
  tecnicoSelecionado?: number;
  //usuarios: Usuario[]=[]; // Suponha que você tenha uma lista de usuários aqui
  usuariosgestor: Usuario[]=[]; // Suponha que você tenha uma lista de usuários aqui
  usuariostecnico: Usuario[]=[]; // Suponha que você tenha uma lista de usuários aqui



  constructor(private client: HttpClient,private router: Router) {}

  ngOnInit() {
    this.client.get<Usuario[]>("https://localhost:7144/EpiList/Usuario/listar").subscribe({
      next: (data) => {
        //this.usuarios = data;
        this.usuariosgestor = data.filter(dado => dado.cargo == "gestor")
        this.usuariostecnico = data.filter(dado => dado.cargo == "tecnico")
      },
      error: (erro) => {
        console.log(erro);
      },
    });
  }

  cadastrarUnidade() {
    this.client.post<Unidade>("https://localhost:7144/EpiList/Unidade/cadastrar")
    // Agora, você pode usar this.unidadeNome, this.gestorSelecionado e this.tecnicoSelecionado
    // para criar uma nova unidade com os dados selecionados.
  }


}

<p>unidade-cadastrar works!</p>
<div>
    <label for="unidadeNome">Nome da Unidade:</label>
    <input id="unidadeNome" [(ngModel)]="unidadeNome" name="unidadeNome" />

    <label for="gestor">Gestor:</label>
    <select id="gestor" [(ngModel)]="gestorSelecionado" name="gestor">
      <option *ngFor="let usuario of usuariosgestor" [value]="usuario.usuarioID">{{usuario.nome}}</option>
    </select>

    <label for="tecnico">Técnico:</label>
    <select id="tecnico" [(ngModel)]="tecnicoSelecionado" name="tecnico">
      <option *ngFor="let usuario of usuariostecnico" [value]="usuario.usuarioID">{{usuario.nome}}</option>
    </select>

    <button (click)="cadastrarUnidade()">Cadastrar Unidade</button>
  </div>
