import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Unidade } from 'src/app/models/unidade.model';

@Component({
  selector: 'app-unidade-listar',
  templateUrl: './unidade-listar.component.html',
  styleUrls: ['./unidade-listar.component.css']
})
export class UnidadeListarComponent {
  unidades: Unidade[] = [];

  constructor(private client: HttpClient,private router: Router){ 

  }
  ngOnInit() : void{
    console.log("O componente foi carregado!");

    this.client.get<Unidade[]>("https://localhost:7144/EpiList/Unidade/listar")
      .subscribe({
        //Requisição com sucesso
        next: (unidade) => {
          this.unidades = unidade;
          console.table(unidade);
        }, 
        //Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      })
  }

}
<table>
    <thead>
      <tr>
        <th>Unidade ID</th>
        <th>Nome</th>
        <th>Equipes</th>
        <th>Usuários</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let unidade of unidades">
        <td>{{ unidade.unidadeID }}</td>
        <td>{{ unidade.nome }}</td>
        <td>
          <ul>
            <li *ngFor="let equipe of unidade.equipes">
              {{ equipe.EquipeID }} - {{ equipe.NomeEquipe }}
            </li>
          </ul>
        </td>
        <td>
          <ul>
            <li *ngFor="let usuario of unidade.usuarios">
              {{ usuario.usuarioID }} - {{ usuario.nomeUsuario }}<br>
              Email: {{ usuario.email }}<br>
              Telefone: {{ usuario.telefone }}<br>
              CPF: {{ usuario.cpf }}<br>
              Cargo: {{ usuario.cargo }}
            </li>
          </ul>
        </td>
        <td>
            <button></button>
        </td>
      </tr>
    </tbody>
  </table>
