import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Epi } from 'src/app/models/epi.model';

@Component({
  selector: 'app-epi-cadastrar',
  templateUrl: './epi-cadastrar.component.html',
  styleUrls: ['./epi-cadastrar.component.css']
})
export class EpiCadastrarComponent {
  constructor(private client: HttpClient,private router: Router) {}
  quantidade!: number;
  descricao!:string;
  c_a!:string;

  cadastrar(): void {
    let epi : Epi={
      descricao : this.descricao,
      c_A: this.c_a,
      quantidade : this.quantidade
    };
    console.log(epi);
    this.client.post<Epi>("https://localhost:7144/EpiList/EPI/cadastrar", epi).subscribe({
      next: (data) => {
        console.log(data);
        this.router.navigate(['pages/Epi/listar']);
      },
      error: (erro) => {
        console.log(erro);
      },
    });
  }
}
<h1>Cadastro de Epi</h1>

    <div>
      <label for="descricao">Descrição:</label>
      <input type="text" id="descricao" name="descricao" [(ngModel)]="descricao" required>
    </div>

    <div>
      <label for="c_a">Código CA:</label>
      <input type="text" id="c_a" name="c_a" [(ngModel)]="c_a" required>
    </div>

    <div>
      <label for="quantidade">Quantidade:</label>
      <input type="number" id="quantidade" name="quantidade" [(ngModel)]="quantidade" required>
    </div>

    <button (click)="cadastrar()">Salvar</button>
