import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Usuario } from 'src/app/models/usuario.model';

@Component({
  selector: 'app-unidade-cadastrar',
  templateUrl: './unidade-cadastrar.component.html',
  styleUrls: ['./unidade-cadastrar.component.css']
})
export class UnidadeCadastrarComponent {
  unidadeForm!: FormGroup;
  usuarios: Usuario[] = [];

  constructor(private fb: FormBuilder, private client: HttpClient,private router: Router) {}

  ngOnInit() {
    this.client.get<Usuario[]>("https://localhost:7144/EpiList/Usuario/listar")
      .subscribe({
        //Requisição com sucesso
        next: (usuario) => {
          this.usuarios = usuario;
          console.table(usuario);
        }, 
        //Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      })


    this.unidadeForm = this.fb.group({
      nome: ['', Validators.required],
      usuariosId: this.fb.array([], Validators.required)
    });
  }

  // adicionarUsuario(cargo: string) {
  //   const selectedUsuarios = this.usuarios.filter(usuario => usuario.cargo === cargo);
  //   const usuariosIdControl = this.unidadeForm.get('usuariosId');
  //   for (const usuario of selectedUsuarios) {
  //     usuariosIdControl.push(this.fb.control(usuario.id));
  //   }
  // }

  cadastrarUnidade() {
    // Enviar os dados do formulário para o servidor
    console.log(this.unidadeForm.value);
    // Você pode usar um serviço para fazer a chamada de API para cadastrar a unidade.
  }
}
