    <div formArrayName="usuariosId">
      <label *ngFor="let usuario of unidadeForm.get('usuariosId').controls">
        <input type="checkbox" [formControlName]="usuario.value"> {{ usuario.value }}
      </label>
    </div>
    <br>
    <button type="submit">Cadastrar Unidade</button>
  </form>
