import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Epi } from 'src/app/models/epi.model';

@Component({
  selector: 'app-epi-listar',
  templateUrl: './epi-listar.component.html',
  styleUrls: ['./epi-listar.component.css']
})
export class EpiListarComponent {

  epi : Epi[] = [];

  constructor(private client: HttpClient){ 
    //Um problema de CORS ao fazer uma requisição para a
    //nossa API
  }

  ngOnInit() : void{
    console.log("O componente foi carregado!");

    this.client.get<Epi[]>("http://localhost:5008/EpiList/EPI/listar")
      .subscribe({
        //Requisição com sucesso
        next: (epi) => {
          this.epi = epi;
          console.table(epi);
        }, 
        //Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      })
  }
}
<h1>Lista de Epis</h1>

<ul *ngFor="let e of epi">
  <li>
    {{ e.Descricao }}
  </li>
</ul>
