import { Component } from '@angular/core';

@Component({
  selector: 'app-equipe-deletar',
  templateUrl: './equipe-deletar.component.html',
  styleUrls: ['./equipe-deletar.component.css']
})
export class EquipeDeletarComponent {

}
<p>equipe-deletar works!</p>
<h2>Confirmação de Exclusão</h2>
<p>Você tem certeza de que deseja excluir este Usuario?</p>
<button (click)="deletarUsuario()">Confirmar Exclusão</button>
<button (click)="cancelar()">Cancelar</button>
<div *ngIf="usuario">
    <p>Remover epi</p>
    <h2>EPIS do Usuário</h2>
    <ul>
      <li *ngFor="let epi of usuario.epis">
        {{ epi.descricao }}
        <button (click)="removerEpi(epi.epiID)">Excluir</button>
      </li>
    </ul>
</div>
