import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Epi } from 'src/app/models/epi.model';

@Component({
  selector: 'app-epi-listar',
  templateUrl: './epi-listar.component.html',
  styleUrls: ['./epi-listar.component.css']
})
export class EpiListarComponent {

  epi : Epi[] = [];

  constructor(private client: HttpClient){ 
    //Um problema de CORS ao fazer uma requisição para a
    //nossa API
  }

  ngOnInit() : void{
    console.log("O componente foi carregado!");

    this.client.get<Epi[]>("https://localhost:7144/EpiList/EPI/listar")
      .subscribe({
        //Requisição com sucesso
        next: (epi) => {
          this.epi = epi;
          console.table(epi);
        }, 
        //Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      })
  }
}
<h1>Lista de Epis</h1>

<table id="table">
  <tr>
    <th>#Id</th>
    <th>Descrição</th>
    <th>Quantidade</th>

    <th>Categoria</th>
    <th>Criado em</th>
  </tr>
  <tr *ngFor="let p of produtos">
    <td>{{ p.produtoId }}</td>
    <td>{{ p.nome }}</td>
    <td>{{ p.descricao }}</td>
    <td>{{ p.quantidade }}</td>
    <td>{{ p.preco | currency : "BRL" }}</td>
    <td>{{ p.categoria?.nome }}</td>
    <td>{{ p.criadoEm | date : "dd/MM/yyyy" }}</td>
  </tr>
</table>
