import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators, FormArray } from '@angular/forms';
import { Router } from '@angular/router';
import { Usuario } from 'src/app/models/usuario.model';

@Component({
  selector: 'app-unidade-cadastrar',
  templateUrl: './unidade-cadastrar.component.html',
  styleUrls: ['./unidade-cadastrar.component.css']
})
export class UnidadeCadastrarComponent {
  unidadeForm!: FormGroup;
  usuarios: Usuario[] = [];

  constructor(private fb: FormBuilder, private client: HttpClient,private router: Router) {}

  ngOnInit() {
    this.client.get<Usuario[]>("https://localhost:7144/EpiList/Usuario/listar")
      .subscribe({
        //Requisição com sucesso
        next: (usuario) => {
          this.usuarios = usuario;
          console.table(usuario);
        }, 
        //Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      })
      this.unidadeForm = this.fb.group({
        nome: ['', Validators.required],
        usuariosId: this.fb.array([], Validators.required)
      });

  }
  adicionarUsuario(usuarioId: string) {
    const usuariosIdControl = this.unidadeForm.get('usuariosId') as FormArray;

    if (usuariosIdControl) {
      const usuario = this.usuarios.find(u => u.usuarioID === usuarioId);
      const cargo = usuario ? usuario.cargo : '';

      // Verifique se um usuário com o mesmo cargo já foi adicionado
      if (usuariosIdControl.value.indexOf(cargo) === -1) {
        usuariosIdControl.push(this.fb.control(cargo));
      }
    }
  }


}

