import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Epi } from 'src/app/models/epi.model';

@Component({
  selector: 'app-epi-deletar',
  templateUrl: './epi-deletar.component.html',
  styleUrls: ['./epi-deletar.component.css']
})
export class EpiDeletarComponent {
  constructor(private client: HttpClient){ 
    //Um problema de CORS ao fazer uma requisição para a
    //nossa API
  }


  deletarEpi(epi : Epi) {
    this.client.delete<Epi[]>("https://localhost:7144/EpiList/EPI/listar")
      .subscribe({
        //Requisição com sucesso
        next: (epi) => {
          this.epi = epi;
          console.table(epi);
        }, 
        //Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      })
  }

}
