import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Epi } from 'src/app/models/epi.model';

@Component({
  selector: 'app-epi-atualizar',
  templateUrl: './epi-atualizar.component.html',
  styleUrls: ['./epi-atualizar.component.css']
})
export class EpiAtualizarComponent {
  constructor(private client: HttpClient,private router: Router, private route: ActivatedRoute) {}
  epiID!: number;
  epi!: Epi;

  ngOnInit(): void{
    this.epiID = this.route.snapshot.params['id'];
    this.client.get<Epi>("https://localhost:7144/EpiList/EPI/listar")
    .subscribe({
      //Requisição com sucesso
      next: (epi) => {
        this.epi = epi;
        console.table(epi);
      }, 
      //Requisição com erro
      error: (erro) => {
        console.log(erro);
      }
    })
  }

}
<h1>Atualizar de Epi</h1>

    <div>
      <label for="descricao">Descrição:</label>
      <input type="text" id="descricao" name="descricao" [(ngModel)]="descricao" required>
    </div>

    <div>
      <label for="c_a">Código CA:</label>
      <input type="text" id="c_a" name="c_a" [(ngModel)]="c_a" required>
    </div>

    <div>
      <label for="quantidade">Quantidade:</label>
      <input type="number" id="quantidade" name="quantidade" [(ngModel)]="quantidade" required>
    </div>

    <button (click)="atualizar()">Salvar</button>
