  //       usuariosIdControl.push(this.fb.control(usuario.cargo));
  //     }
  //   }
  // }
  adicionarUsuario(usuarioId: string) {
    const usuariosIdControl = this.unidadeForm.get('usuariosId') as FormArray;

    if (usuariosIdControl) {
      // Verifique se o usuário já foi adicionado
      if (usuariosIdControl.value.indexOf(usuarioId) === -1) {
        usuariosIdControl.push(this.fb.control(usuarioId));
      }
    }
  }



  cadastrarUnidade() {
    console.log(this.unidadeForm.value);
    // Você pode usar um serviço para fazer a chamada de API para cadastrar a unidade.
  }
}

