import { HttpClient } from '@angular/common/http';
import { ChangeDetectorRef, Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Usuario } from 'src/app/models/usuario.model';

@Component({
  selector: 'app-usuario-deletar',
  templateUrl: './usuario-deletar.component.html',
  styleUrls: ['./usuario-deletar.component.css']
})
export class UsuarioDeletarComponent {
  constructor(private client: HttpClient, private route: ActivatedRoute,private router: Router) { }
  //id!: string;
  id = this.route.snapshot.params['id']; // Obtém o ID do Usuario da rota
  usuario: any;
  ngOnInit(): void{
    this.client.get<Usuario>("https://localhost:7144/EpiList/Usuario/"+this.id)
    .subscribe({
      next: (usu) => {
        console.log(usu);
        this.usuario=usu;
      }, 
      error: (erro) => {
        console.log(erro);
      }
    })
  }

   : (e) => {
          console.log(e);
          // Realize qualquer outra ação após a exclusão, se necessário
          this.router.navigate(['pages/Epi/listar']);
        },
        // Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      });
  }
  cancelar(){
    this.router.navigate(['pages/Epi/listar']);
  }



  removerEpi(epiId: number) {
    const epiIds = [epiId];

    this.client.put<string>("https://localhost:7144/EpiList/Usuario/" + this.id + "/RemoverEPIs", epiIds)
      .subscribe({
        // Requisição com sucesso
        next: (e) => {
          console.log(e);
        },
        // Requisição com erro
        error: (erro) => {
          console.log(erro);
        }
      });
      this.router.navigate(['pages/Epi/listar']);
  }

}
<h2>Confirmação de Exclusão</h2>
<p>Você tem certeza de que deseja excluir este Usuario?</p>
<button (click)="deletarUsuario()">Confirmar Exclusão</button>
<button (click)="cancelar()">Cancelar</button>
<div *ngIf="usuario">
    <p>Remover epi</p>
    <h2>EPIS do Usuário</h2>
    <ul>
      <li *ngFor="let epi of usuario.epis">
        {{ epi.descricao }}
        <button (click)="removerEpi(epi.epiID)">Excluir</button>
      </li>
    </ul>
</div>
