import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Epi } from 'src/app/models/epi.model';

@Component({
  selector: 'app-usuario-cadastrar',
  templateUrl: './usuario-cadastrar.component.html',
  styleUrls: ['./usuario-cadastrar.component.css']
})
export class UsuarioCadastrarComponent {
  nome!: string;
  email!: string;
  telefone!: string;
  senha!: string;
  cpf!: string;
  cargo!: string;
  episSelecionados: number[] = [];
  episDisponiveis: Epi[] = [];
  constructor(private client: HttpClient,private router: Router) {}

  ngOnInit(): void {
    // Busque a lista de EPIS disponíveis
    this.epiService.getEpisDisponiveis().subscribe((epis: Epi[]) => {
      this.episDisponiveis = epis;
    });
  }

  cadastrar(): void {
    const usuario = {
      nome: this.nome,
      email: this.email,
      telefone: this.telefone,
      senha: this.senha,
      cpf: this.cpf,
      cargo: this.cargo,
      episId: this.episSelecionados
    };

    this.client.post<any>('http://localhost:5008/EpiList/Usuario/cadastrar', usuario).subscribe({
      next: (data) => {
        console.log(data);
        this.router.navigate(['pages/Usuario/listar']);
      },
      error: (erro) => {
        console.log(erro);
      },
    });
  }

}
<h1>Cadastro de Usuário</h1>

<div>
  <label for="nome">Nome:</label>
  <input type="text" id="nome" name="nome" [(ngModel)]="nome" required>
</div>

<div>
  <label for="email">Email:</label>
  <input type="email" id="email" name="email" [(ngModel)]="email" required>
</div>

<div>
  <label for="telefone">Telefone:</label>
  <input type="tel" id="telefone" name="telefone" [(ngModel)]="telefone" required>
</div>

<div>
  <label for="senha">Senha:</label>
  <input type="password" id="senha" name="senha" [(ngModel)]="senha" required>
</div>

<div>
  <label for="cpf">CPF:</label>
  <input type="text" id="cpf" name="cpf" [(ngModel)]="cpf" required>
</div>

<div>
  <label for="cargo">Cargo:</label>
  <input type="text" id="cargo" name="cargo" [(ngModel)]="cargo" required>
</div>

<div>
  <label for="epis">EPIS:</label>
  <select id="epis" name="epis" [(ngModel)]="episSelecionados" multiple>
    <!-- Use ngFor para listar os EPIS disponíveis e permitir a seleção múltipla -->
    <option *ngFor="let epi of episDisponiveis" [value]="epi.epiID">{{ epi.descricao }}</option>
  </select>
</div>

<button (click)="cadastrar()">Cadastrar</button>
