      <label for="descricao">Descrição:</label>
      <input type="text" id="descricao" formControlName="descricao" required>
    </div>
    <div>
      <label for="c_A">C_A:</label>
      <input type="text" id="c_A" formControlName="c_A" required>
    </div>
    <div>
      <label for="quantidade">Quantidade:</label>
      <input type="number" id="quantidade" formControlName="quantidade" required>
    </div>
    <button type="submit">Salvar Edição</button>
  </form>
