import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-equipe-atualizar',
  templateUrl: './equipe-atualizar.component.html',
  styleUrls: ['./equipe-atualizar.component.css']
})
export class EquipeAtualizarComponent {
  usuarioId= this.route.snapshot.params['id'];
  equipeForm: FormGroup;
  constructor(private client: HttpClient,private router: Router, private route: ActivatedRoute, private fb: FormBuilder) 
  {
    this.equipeForm = this.fb.group({
      nomeEquipe: [''],
    });
  }
  ngOnInit(): void{

    this.client.get<Usuario>("https://localhost:7144/EpiList/Usuario/"+this.usuarioId)
    .subscribe({
      next: (usu) => {
        console.table(usu);

        this.usuarioForm.patchValue({
          nome: usu.nome,
          email: usu.email,
          telefone: usu.telefone,
          cpf: usu.cpf,
          senha: usu.senha,
          cargo: usu.cargo,
          equipeID: [usu.equipe]
        });
      }, 
      error: (erro) => {
        console.log(erro);
      }
    })
    this.client.get<Epi[]>("https://localhost:7144/EpiList/EPI/listar")
    .subscribe({
      //Requisição com sucesso
      next: (epi) => {
        this.episDisponivel=epi;
        console.table(epi);
      }, 
      //Requisição com erro
      error: (erro) => {
        console.log(erro);
      }
    })

  }

}
